$(document)
  .ready(function() {
    $(document).on('click', 'a[href^="#"]', function (event) {
        event.preventDefault();

        $('html, body').animate({
            scrollTop: $($.attr(this, 'href')).offset().top
        }, 500);
    });

    $('.ui.dropdown')
      .dropdown({
        on: 'hover'
      })
    ;

    $('.anim')
      .transition('scale in')
    ;

    var mobile_menu_list = $("#mobile-menu-list");
    $("#mobile-menu-trigger").on("click", function(){
      mobile_menu_list.transition('fade up');
    });

  })
;

function openUrl(url, type) {
  type = type || "_self";
  window.open(url, type);
}
